推荐: $js.toString(() => {
let kdata = fetch(input).split('data\\\":')[3].split(',\\\"newestShort')[0] + '}';
kdata = kdata.replace(/\\/g, '');
let kjson = JSON.parse(kdata);
VODS = [];
Object.keys(kjson).forEach((key) => {
    kjson[key].list.map((it) => {
        VODS.push({
            vod_name: it.vodName,
            vod_pic: it.vodPic,
            vod_remarks: it.vodRemarks+'_'+it.vodDoubanScore,
            vod_id: it.vodId
        })
    })       
})        
}),
